            if (graphNodes.length > 0 && graphLinks.length > 0 ) {
              await generateAndUpdateDocumentation(repoName, repoId, graphNodes, graphLinks, graphFolders)
            }
        }
      }
      return c.json({ message: 'Graph already up to date' }, 200)
    }

    await sql`
      UPDATE graphs 
      SET 
        status = 'updating'
      WHERE id = ${graphId}
    `

    const rows = await sql`
      SELECT 
        id
      FROM repositories
      WHERE git_provider = ${gitProvider}
        AND repo_org = ${repoOrg}
        AND repo_name = ${repoName}
        AND branch = ${branch}
        AND commit_hash = ${commitHash}
    `

    if (rows.length > 0) {
      await sql`
        UPDATE graphs 
        SET 
          status = 'completed',
          repo_id = ${rows[0].id}
        WHERE id = ${graphId}
      `

      return c.json({ message: 'Graph updated' }, 200)
    }

    updateGraph({
      gitProvider,
      repoOrg,
      repoName,
      branch,
      accessToken,
      commitHash,
      gitlabRepoId,
      graphId,
      generateDocBool
    })

    return c.json({ message: 'Graph updating' }, 200)
  } catch (error) {
    console.log('Error updating graph', error)
    return c.json({ error: 'Error updating graph' }, 500)
  }
})

