      similarNodes.push(` - Assignments: ${topAssignments}`)
    }
  } else {
    similars = topNSimilar(name, nodes.filter((n) => n.type === type), n)
    if (similars.length > 0) {
      similarNodes.push(
        ` - ${type.charAt(0).toUpperCase() + type.slice(1)}: ${similars}`,
