  userOrgId,
  graphId,
}: {
  userOrgId: string
  graphId: string
}): Promise<GraphFolder[]> {
  try {
    const rows = await sql<GraphFolder[]>`
      SELECT
        f.id,
        f.name,
        f.wiki
      FROM graphs g
      JOIN repositories r
        ON r.id = g.repo_id
      JOIN graph_folders f
        ON f.repo_id = r.id
      WHERE g.id = ${graphId}
        AND g.org_id = ${userOrgId}
        AND g.status = 'completed'
    `
    return rows
  } catch (error) {
    console.log('Error getting graph folder by id', error)
    return []
  }
}

