async function downloadAndExtractRepo(
  gitService: GitServiceType,
  repoOrg: string,
  repoName: string,
  branch: string,
  accessToken: string,
  commitSha: string,
  gitlabRepoId?: number
): Promise<string> {
  let url

  const headers: Record<string, string> = {
    Authorization: `Bearer ${accessToken}`
  }

  switch (gitService) {
    case 'github':
      url = `https://api.github.com/repos/${repoOrg}/${repoName}/zipball/${branch}`

      headers['Accept'] = 'application/vnd.github+json'
      headers['X-GitHub-Api-Version'] = '2022-11-28'
      break
    case 'gitlab':
      url = `https://gitlab.com/api/v4/projects/${gitlabRepoId}/repository/archive.zip?sha=${branch}`

      break
    case 'bitbucket':
      url = `https://bitbucket.org/${repoOrg}/${repoName}/get/${branch}.zip`

      break
  }

  try {
    const response = await axios({
      method: 'GET',
      url,
      responseType: 'arraybuffer',
      headers
    })

    const tmpFolderPath = `${process.cwd()}/tmp`
    await fs.mkdir(tmpFolderPath, { recursive: true })

    const extractPath = path.join(
      tmpFolderPath,
      `${commitSha}_${repoOrg}_${repoName}_${branch}_${performance.now()}`
    )

    // Save zip
    const zipPath = `${extractPath}.zip`
    await fs.writeFile(zipPath, response.data)

    // Extract zip
    const zip = new AdmZip(zipPath)

    const zipEntries = zip.getEntries()

    const mainFolderPath = zipEntries.find(
      (entry: any) =>
        entry.isDirectory &&
        entry.entryName.endsWith('/') &&
        (entry.entryName.match(/\//g) || []).length == 1
    )?.entryName
    zip.extractAllTo(extractPath, true)

    // Delete zip
    await fs.unlink(zipPath)

    const finalPath = path.join(extractPath, mainFolderPath ?? '')
    const totalSize = (await getTotalSize(finalPath)) / 1024 / 1024
    console.log(`${totalSize} MB`)
    if (totalSize >= MAXSIZE) {
      await fs.rm(extractPath, { recursive: true, force: true })
      throw new Error(
        `Repository size is too large: ${totalSize.toFixed(2)} MB. Max size is ${MAXSIZE} MB.`
      )
    }
    return finalPath
  } catch (error) {
    console.log(error)
    throw error
  }
}

