    let userPrompt = `Generate a publication for the ${folderContext}. The following information corresponds to the documentation of the subfolders of the folder we want to document. Use them to generate a better response. Here they are:\n\n`;

    for (const [subfolder, subfolderDoc] of Object.entries(
