async function generateNodeDocumentation(
  node: GraphNode,
  nodes: GraphNode[],
  graph: Graph,
  repoName: string,
  model: string
) {
  const { systemPrompt, userPrompt } = generateNodePrompts(
    node,
    nodes,
    graph,
    repoName
  );

  try {
    const messages: chatCompletionMessages = [
      { role: "system", content: systemPrompt },
      { role: "user", content: userPrompt },
    ];

    if (
      ["class", "function", "method"].includes(node.type) ||
      node.code.split("\n").length >= 2
    ) {
      const { response, tokens } = await getOpenAIChatCompletion(
        messages,
        node.type === "file" ? "gpt-4o" : model
      );
      totalTokens += tokens ?? 0;
      node.generatedDocumentation = response;
    } else {
      node.generatedDocumentation = `Code: ${node.code}`;
    }
    // console.log(`#### ${node.label} ####`)
    // console.log({ systemPrompt, userPrompt } )
    // console.log({ response, tokens })
  } catch (error: any) {
    console.error(
      `Error generating documentation for ${node.label}: ${error.message}`
    );
  }
}

