function generateNodePrompts(
  node: GraphNode,
  nodes: GraphNode[],
  graph: Graph,
  repoName: string
): { systemPrompt: string; userPrompt: string } {
  const originFileNode = findFileParentNode(nodes, node);

  let systemPrompt = "";
  if (node.type !== "file") {
    systemPrompt = `You are a helpful ${node.language} code assistant that helps to write code documentation for the repository ${repoName} in just one paragraph, mentioning the principal features of the code and being concise but precise and accurate.`;
  } else {
    systemPrompt = `You are a helpful ${node.language} code assistant that helps to write summaries for files from the repository ${repoName}. The user will pass you a reduced version of the file content and you must explain the main features and purpose of the file. Be concise but accurate and precise.`;
  }

  if (["function", "class", "method"].includes(node.type)) {
    systemPrompt += ` The documentation must include how each parameter is used and what the ${node.type} does or accomplishes.`;
  }

  if (node.type !== "file")
    systemPrompt += ` Prevent any prose in your response. Please, be concise and don't talk about the file.`;

  const parentFileString = originFileNode
    ? `from file "${originFileNode.label}" `
    : "";

  let userPrompt = "";
  if (node.type !== "file") {
    userPrompt = `Write a documentation for the ${node.type} called "${node.fullName}" ${parentFileString}in just one paragraph, mentioning the principal features of the code:`;
  } else {
    const folder = node.fullName.split("\\").slice(0, -1).join("\\");
    userPrompt = `Write a brief documentation for the file "${node.label}" from folder "${folder}", explaining the main features and purpose of the file:`;
  }

  const code = [
    "method",
    "function",
    "interface",
    "assignment",
    "type",
    "enum",
    "struct",
    "union",
  ].includes(node.type)
    ? node.code
    : node.codeNoBody;

  if (
    originFileNode &&
    graph[node.id].length > 0 &&
    originFileNode.importStatements
  ) {
    userPrompt += `\n\`\`\`${node.language}\n${originFileNode.importStatements}\n\n${code}\n\`\`\`\n\n`;
    systemPrompt += ` Don't mention about the imports if "${node.label}" is not using it directly in its implementation.`;
  } else {
    userPrompt += `\n\`\`\`${node.language}\n${code}\n\`\`\`\n\n`;
  }

  const linkedNodes = graph[node.id].map((linkedNodeId) =>
    nodes.find((node) => node.id === linkedNodeId)
  );

  if (
    graph[node.id].length > 0 &&
    linkedNodes.some((n) => n?.generatedDocumentation)
  ) {
    userPrompt += `Use the following information to generate a better description of what ${node.label} does:`;
    systemPrompt += ` Do not verbose about the extra information, just use them as a reference to explain what ${node.label} does.`;

    graph[node.id].forEach((linkedNodeId) => {
      const linkedNode = nodes.find((n) => n.id === linkedNodeId);
      if (linkedNode && linkedNode.generatedDocumentation) {
        userPrompt += `\n- ${linkedNode.label}: ${linkedNode.generatedDocumentation}`;
      }
    });
  }

  userPrompt += `Remember to not verbose about the extra information, just use them as a reference to explain what "${node.label}" does.`;
  if (node.type === "file") {
    userPrompt += " Remember also to explain the purpose of the file.";
  }
  return { systemPrompt, userPrompt };
}

